import { Button } from '@gentleduck/registry-ui-duckui/button'
import { CommandShortcut } from '@gentleduck/registry-ui-duckui/command'
import { toast } from 'sonner'

export default function ButtonDemo() {
  return (
    <Button aria-label="Inbox button with keyboard shortcut" type="button">
      Button
      <CommandShortcut
        variant="secondary"
        key="alt+k"
        onKeysPressed={() => {
          toast('Your inbox has been updated', {
            description: `you have clicked the 'command' button`,
          })
        }}>
        alt+k
      </CommandShortcut>
    </Button>
  )
}
