'use client'

import { useState } from 'react'

import {
  Select,
  SelectContent,
  SelectGroup,
  SelectItem,
  SelectLabel,
  SelectTrigger,
  SelectValue,
} from '@gentleduck/registry-ui-duckui/select'
import { cva } from '@gentleduck/variants'
import { cn } from '@gentleduck/libs/cn'
import { Button } from '@gentleduck/registry-ui-duckui/button'

// CVA definition for a box
const boxVariants = cva('flex items-center justify-center rounded-lg text-white', {
  variants: {
    variant: {
      primary: 'bg-blue-500',
      success: 'bg-green-500',
      danger: 'bg-red-500',
      warning: 'bg-yellow-500 text-black',
    },
  },
  defaultVariants: {
    variant: 'primary',
  },
})

export default function CvaDemo() {
  const [variant, setVariant] = useState<'primary' | 'success' | 'danger' | 'warning'>('primary')

  return (
    <div className="flex items-center gap-6 p-6">
      {/* Select to choose a variant */}
      <Select onValueChange={(val) => setVariant(val as any)}>
        <SelectTrigger className="w-[200px]">
          <SelectValue placeholder="Choose a Variant" />
        </SelectTrigger>
        <SelectContent>
          <SelectGroup>
            <SelectLabel>Variants</SelectLabel>
            <SelectItem value="primary">Primary</SelectItem>
            <SelectItem value="success">Success</SelectItem>
            <SelectItem value="danger">Danger</SelectItem>
            <SelectItem value="warning">Warning</SelectItem>
          </SelectGroup>
        </SelectContent>
      </Select>

      {/* Box preview */}
      <Button className={cn(boxVariants({ variant }))}>{variant.toUpperCase()}</Button>
    </div>
  )
}
