import { cn } from '@gentleduck/libs/cn'
import { groupArrays } from '@gentleduck/libs/group-array'
import * as React from 'react'
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuPortal,
  DropdownMenuRadioGroup,
  DropdownMenuRadioItem,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuSub,
  DropdownMenuSubContent,
  DropdownMenuSubTrigger,
  DropdownMenuTrigger,
} from './_dropdown-menu/dropdown-menu'
import { Button, type ButtonProps, buttonVariants, type CommandType } from '../button'

export interface DropdownMenuOptionsDataType
  extends Partial<
    React.ComponentPropsWithoutRef<typeof DropdownMenuCheckboxItem> &
      React.ComponentPropsWithoutRef<typeof DropdownMenuItem> &
      React.ComponentPropsWithoutRef<typeof DropdownMenuRadioItem>
  > {
  actionType: 'drawer' | 'dialog' | 'sheet' | 'item'
  command?: React.ComponentPropsWithoutRef<typeof DropdownMenuShortcut> & CommandType
  icon?: React.ReactNode
  nestedData?: React.ComponentPropsWithoutRef<typeof DropdownMenuSubContent> & DropdownMenuOptionsType
}

export interface DropdownMenuOptionsType {
  itemType: 'checkbox' | 'radio' | 'label'
  optionsData: DropdownMenuOptionsDataType[]
  group?: number[]
}

export interface DropdownMenuViewProps extends React.ComponentPropsWithoutRef<typeof DropdownMenu> {
  trigger: React.ComponentPropsWithoutRef<typeof DropdownMenuTrigger> & ButtonProps
  content: {
    label?: React.ComponentPropsWithoutRef<typeof DropdownMenuLabel>
    options: DropdownMenuOptionsType
  } & React.ComponentPropsWithoutRef<typeof DropdownMenuContent>
}

export type ItemActionOpenState = {
  id: `${DropdownMenuOptionsDataType['actionType']}-${number}`
  value: boolean
}
export interface DropdownMenuContextType {
  open: ItemActionOpenState | null
  setOpen: React.Dispatch<React.SetStateAction<ItemActionOpenState | null>>
}

export const DropdownMenuContext = React.createContext<DropdownMenuContextType | null>(null)

export const useDropdownMenuContext = () => {
  const context = React.useContext(DropdownMenuContext)
  if (!context) {
    throw new Error('useDropdownMenuContext must be used within a DropdownMenuContextProvider')
  }
  return context
}

export const DropdownMenuProvider = ({ children }: { children: React.ReactNode }) => {
  const [open, setOpen] = React.useState<ItemActionOpenState | null>(null)
  return <DropdownMenuContext.Provider value={{ open, setOpen }}>{children}</DropdownMenuContext.Provider>
}

export function DropdownMenuView({ content, trigger, ...props }: DropdownMenuViewProps) {
  const { className: optionsClassName, options, label, align = 'end', ...contentProps } = content ?? {}

  const groupedOption = groupArrays(options?.group ?? [options?.optionsData?.length || 1], options?.optionsData ?? [])

  return (
    <DropdownMenu {...props}>
      <DropdownWrapperTrigger trigger={trigger} />

      <DropdownMenuContent align={align} className={cn('w-[200px]', optionsClassName)} {...contentProps}>
        <DropdownWrapperLabel label={label} />

        {groupedOption.map((group, idx) => {
          return (
            <React.Fragment key={`group-${idx}`}>
              {group.map((item, idx) => {
                const { nestedData, key: _key } = item

                const {
                  className: nestedClassName,
                  group: nestedGroup,
                  optionsData: nestedOptions,
                  ...nestedProps
                } = nestedData ?? {}

                const groupedNestedOption =
                  groupArrays(
                    nestedData?.group ?? [nestedData?.optionsData?.length || 1],
                    nestedData?.optionsData ?? [],
                  ) ?? []

                return (
                  <React.Fragment key={`item-${idx}`}>
                    {nestedData?.optionsData?.length ? (
                      <DropdownMenuSub key={`sub-item-${idx}`}>
                        <DropdownWrapperSubTrigger trigger={item} />
                        <DropdownMenuPortal>
                          <DropdownMenuSubContent className={cn('w-[200px]', nestedClassName)} {...nestedProps}>
                            {groupedNestedOption?.map((nestedItem, idx) => {
                              return (
                                <React.Fragment key={`nested-${idx}`}>
                                  <DropdownWrapperSubContent itemType={nestedData?.itemType} nestedData={nestedItem} />
                                  {idx !== groupedNestedOption?.length - 1 && (
                                    <DropdownMenuSeparator key={`separator-${idx}`} />
                                  )}
                                </React.Fragment>
                              )
                            })}
                          </DropdownMenuSubContent>
                        </DropdownMenuPortal>
                      </DropdownMenuSub>
                    ) : (
                      <DropdownWrapperContentItem idx={idx} item={item} itemType={options.itemType} />
                    )}
                  </React.Fragment>
                )
              })}
              {idx !== groupedOption.length - 1 && <DropdownMenuSeparator />}
            </React.Fragment>
          )
        })}
      </DropdownMenuContent>
    </DropdownMenu>
  )
}

export function DropdownWrapperTrigger({
  trigger,
}: {
  trigger: React.ComponentPropsWithoutRef<typeof DropdownMenuTrigger> & ButtonProps
}) {
  return (
    <DropdownMenuTrigger asChild>
      <Button variant="ghost" size="sm" {...trigger} />
    </DropdownMenuTrigger>
  )
}

export function DropdownWrapperLabel({ label }: { label?: React.ComponentProps<typeof DropdownMenuLabel> }) {
  const { className, ...props } = label ?? {}
  return (
    label && (
      <>
        <DropdownMenuLabel className={cn('text-sm', className)} {...props} />
        <DropdownMenuSeparator />
      </>
    )
  )
}

export function DropdownWrapperContentItem({
  item,
  itemType,
  idx,
}: {
  item: DropdownMenuOptionsDataType
  itemType: 'checkbox' | 'radio' | 'label'
  idx: number
}) {
  const {
    children,
    className,
    value,
    nestedData,
    actionType,
    key: _key,
    onClick,
    //FIX: removed this hence when i clicked on the item it is always focusing.
    autoFocus,
    ...props
  } = item

  const { setOpen } = useDropdownMenuContext()

  const Component =
    itemType === 'checkbox' ? DropdownMenuCheckboxItem : itemType === 'radio' ? DropdownMenuRadioItem : DropdownMenuItem

  return (
    <Component
      value={value as string}
      className={cn(
        buttonVariants({
          variant: 'ghost',
          size: 'sm',
          className: 'flex items-center justify-start gap-2 px-2',
        }),
        itemType === 'checkbox' && 'pl-8',
        itemType === 'radio' && 'pl-8 [&_span_svg]:w-[.5rem]',
        className,
      )}
      onClick={(e) => {
        onClick?.(e)
        setOpen({
          id: `${actionType}-${idx}`,
          value: true,
        })
      }}
      {...props}>
      {item.icon && item.icon}
      {children}
      {item.command?.label && (
        <>
          <DropdownMenuShortcut {...item.command} children={item.command.children} />
          <Button
            className="sr-only hidden"
            command={{
              ...item.command,
              action: () => {
                setOpen({
                  id: `${actionType}-${idx}`,
                  value: true,
                })
                item.command?.action?.()
              },
            }}
          />
        </>
      )}
    </Component>
  )
}

export function DropdownWrapperSubTrigger({ trigger }: { trigger: DropdownMenuOptionsDataType }) {
  const { children, className, icon, ...props } = trigger
  return (
    // @ts-ignore
    <DropdownMenuSubTrigger
      className={cn(
        buttonVariants({
          variant: 'ghost',
          size: 'sm',
          className: 'w-full gap-2 px-2',
        }),
      )}
      {...props}>
      {icon}
      <span>{children}</span>
    </DropdownMenuSubTrigger>
  )
}

export function DropdownWrapperSubContent({
  nestedData,
  itemType,
}: {
  nestedData: DropdownMenuOptionsDataType[]
  itemType: 'checkbox' | 'radio' | 'label'
}) {
  const Children = nestedData.map((item, idx) => {
    const { children, value, icon: Icon, className, ...props } = item
    const { children: commandChildren, ...commandProps } = item.command ?? {}

    const Component =
      itemType === 'checkbox'
        ? DropdownMenuCheckboxItem
        : itemType === 'radio'
          ? DropdownMenuRadioItem
          : DropdownMenuItem

    return (
      <Component
        value={value as string}
        checked={true}
        key={`nested-item-${idx}`}
        className={cn(
          buttonVariants({
            variant: 'ghost',
            size: 'sm',
          }),
          'flex items-center justify-start gap-2',
          itemType !== 'label' && 'pl-8',
          itemType === 'radio' && '[&_span_svg]:w-[.5rem]',
          className,
        )}
        {...props}>
        {Icon && Icon}
        {children}
        {item.command && (
          <>
            <DropdownMenuShortcut children={commandChildren} {...commandProps} key={`nested-item-shortcut-${idx}`} />
            <Button command={item.command} className="sr-only hidden" />
          </>
        )}
      </Component>
    )
  })

  return itemType === 'radio' ? <DropdownMenuRadioGroup value="date">{Children}</DropdownMenuRadioGroup> : Children
}

import React from 'react'
import { DropdownMenuContext } from './dropdown-menu'
import { initRefs } from './dropdown-menu.libs'

export const useDropdownMenuContext = () => {
  const context = React.useContext(DropdownMenuContext)
  if (!context) {
    throw new Error('useDropdownMenuContext must be used within a DropdownMenu')
  }
  return context
}

export function useDropdownMenuInit(open: boolean, onOpenChange: (open: boolean) => void, sub: boolean) {
  const triggerRef = React.useRef<HTMLButtonElement>(null)
  const contentRef = React.useRef<HTMLDivElement>(null)
  const wrapperRef = React.useRef<HTMLDivElement>(null)
  const groupsRef = React.useRef<HTMLDivElement[]>([])
  const itemsRef = React.useRef<HTMLLIElement[]>([])
  const originalItemsRef = React.useRef<HTMLLIElement[]>([])
  const selectedItemRef = React.useRef<HTMLLIElement | null>(null)

  React.useEffect(() => {
    setTimeout(() => {
      initRefs(groupsRef, contentRef, selectedItemRef, itemsRef, originalItemsRef, onOpenChange, sub)
    }, 0)
  }, [open])

  return {
    wrapperRef,
    triggerRef,
    contentRef,
    groupsRef,
    itemsRef,
    originalItemsRef,
    selectedItemRef,
  }
}

import type React from 'react'
import { dstyleItem } from '../command/command.libs'

export function initRefs(
  groupsRef: React.RefObject<HTMLDivElement[] | null>,
  contentRef: React.RefObject<HTMLDivElement | null>,
  selectedItemRef: React.RefObject<HTMLLIElement | null>,
  itemsRef: React.RefObject<HTMLLIElement[]>,
  originalItemsRef: React.RefObject<HTMLLIElement[]>,
  onOpenChange: (open: boolean) => void,
  sub: boolean,
) {
  const groups = contentRef.current?.querySelectorAll('[duck-dropdown-menu-group]')
  const items = contentRef.current?.querySelectorAll(
    sub
      ? '[duck-dropdown-menu-item]:not([duck-dropdown-menu-sub-trigger]):not([aria-disabled])'
      : `[duck-dropdown-menu-item]:not([aria-disabled]):not([duck-dropdown-menu-sub-content] [duck-dropdown-menu-item]), [duck-dropdown-menu-sub-trigger]`,
  ) as unknown as HTMLLIElement[]

  groupsRef.current = Array.from(groups ?? []) as HTMLDivElement[]
  itemsRef.current = Array.from(items ?? []) as HTMLLIElement[]
  originalItemsRef.current = Array.from(items ?? []) as HTMLLIElement[]

  for (let i = 0; i < itemsRef.current?.length; i++) {
    const item = itemsRef.current[i] as HTMLLIElement

    item.addEventListener('mouseenter', () => {
      for (let i = 0; i < itemsRef.current?.length; i++) {
        const item = itemsRef.current[i] as HTMLLIElement
        dstyleItem(item)
      }

      item.setAttribute('aria-selected', '')
      item.focus()
      selectedItemRef.current = item
    })

    if (!item.hasAttribute('duck-dropdown-menu-sub-trigger')) {
      item.addEventListener('click', () => {
        onOpenChange(false)
      })
    }
  }
}

'use client'

import { usePopoverContext } from '@gentleduck/duck-primitives/popover'
import { cn } from '@gentleduck/libs/cn'
import { Check, ChevronRight } from 'lucide-react'
import * as React from 'react'
import { Button } from '../button'
import { useHandleKeyDown } from '../command'
import { Popover, PopoverContent, PopoverTrigger } from '../popover'
import { RadioGroup, RadioGroupItem } from '../radio-group'
import { useDropdownMenuContext, useDropdownMenuInit } from './dropdown-menu.hooks'
import type {
  DropdownMenuContextType,
  DropdownMenuShortcutProps,
  DropdownMenuSubContextType,
} from './dropdown-menu.types'

export const DropdownMenuContext = React.createContext<DropdownMenuContextType | null>(null)

function DropdownMenuImpritive({ children, className, ...props }: React.HTMLProps<HTMLDivElement>) {
  const { open = false, setOpen: onOpenChange = () => {} } = usePopoverContext()
  const { wrapperRef, contentRef, triggerRef, groupsRef, itemsRef, selectedItemRef, originalItemsRef } =
    useDropdownMenuInit(open, onOpenChange, false)

  useHandleKeyDown({
    open,
    itemsRef,
    selectedItem: selectedItemRef.current,
    setSelectedItem: (item) => {
      selectedItemRef.current = item
    },
    originalItemsRef,
    allowAxisArrowKeys: true,
  })

  return (
    <DropdownMenuContext.Provider
      value={{
        open,
        onOpenChange,
        wrapperRef,
        triggerRef: triggerRef,
        contentRef: contentRef,
        groupsRef,
        itemsRef,
        selectedItemRef,
        originalItemsRef,
      }}>
      <div className={cn('relative', className)} duck-dropdown-menu="" {...props} ref={wrapperRef}>
        {children}
      </div>
    </DropdownMenuContext.Provider>
  )
}

function DropdownMenu({ children, ...props }: React.ComponentPropsWithRef<typeof Popover>) {
  return (
    <Popover {...props}>
      <DropdownMenuImpritive {...props}>{children}</DropdownMenuImpritive>
    </Popover>
  )
}

function DropdownMenuTrigger({
  className,
  children,
  asChild,
  onClick,
  ...props
}: React.ComponentPropsWithoutRef<typeof PopoverTrigger>) {
  const { triggerRef } = useDropdownMenuContext()
  return (
    <PopoverTrigger
      ref={triggerRef as never}
      className={cn(className)}
      asChild={asChild}
      {...props}
      duck-select-trigger="">
      {children}
    </PopoverTrigger>
  )
}

function DropdownMenuContent({
  children,
  className,
  renderOnce = true,
  ...props
}: React.ComponentPropsWithoutRef<typeof PopoverContent> & {
  renderOnce?: boolean
}): React.JSX.Element {
  const { contentRef } = useDropdownMenuContext()
  return (
    <PopoverContent
      ref={contentRef}
      duck-dropdown-menu-content=""
      className={cn('min-w-[8rem] overflow-visible p-1', className)}
      {...props}>
      {children}
    </PopoverContent>
  )
}

function DropdownMenuLabel({
  className,
  ref,
  inset,
  ...props
}: React.HTMLProps<HTMLDivElement> & { inset?: boolean }): React.JSX.Element {
  return (
    <div
      ref={ref}
      className={cn('px-2 py-1.5 font-semibold text-sm', inset && 'pl-8', className)}
      {...props}
      duck-dropdown-menu-label=""
    />
  )
}

function DropdownMenuItem({
  className,
  inset,
  disabled,
  ref,
  ...props
}: React.ComponentPropsWithRef<typeof Button> & { inset?: boolean }): React.JSX.Element {
  return (
    <Button
      ref={ref}
      variant={'ghost'}
      size={'sm'}
      duck-dropdown-menu-item=""
      aria-disabled={disabled}
      disabled={disabled}
      className={cn(
        'h-auto w-full cursor-default justify-start px-2 focus:bg-secondary',
        'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-transparent',
        inset && 'pl-8',
        className,
      )}
      {...props}
    />
  )
}

function DropdownMenuShortcut({
  className,
  keys,
  onKeysPressed,
  ref,
  colored = false,
  ...props
}: DropdownMenuShortcutProps): React.JSX.Element {
  // useKeyCommands({
  //   [keys]: {
  //     name: keys,
  //     description: keys,
  //     execute: () => onKeysPressed(),
  //   },
  // })

  return (
    <kbd
      className={cn(
        'focus:offset-2 [&_svg]:!size-3 !font-sans pointer-events-none ml-auto inline-flex cursor-none select-none items-center gap-[2px] rounded-[4px] px-2 py-[.12rem] text-[.7rem] text-muted-foreground text-secondary-foreground text-xs tracking-widest transition-colors focus:outline-hidden focus:ring-2 focus:ring-ring',
        colored ? 'bg-muted' : 'ltr:-mr-2 rtl:-ml-2',
        className,
      )}
      duck-data-dropdown-menu-shortcut=""
      ref={ref}
      {...props}
    />
  )
}

function DropdownMenuSeparator({ className, ref, ...props }: React.HTMLProps<HTMLDivElement>): React.JSX.Element {
  return (
    <div ref={ref} className={cn('-mx-1 my-1 h-px bg-muted', className)} {...props} duck-dropdown-menu-separator="" />
  )
}

function DropdownMenuGroup({ className, ...props }: React.HTMLProps<HTMLDivElement>): React.JSX.Element {
  return <div className={cn(className)} {...props} duck-dropdown-menu-group="" />
}

export const DropdownMenuSubContext = React.createContext<DropdownMenuSubContextType | null>(null)
export function useDropdownMenuSubContext() {
  const context = React.useContext(DropdownMenuSubContext)
  if (!context) {
    throw new Error('useDropdownMenuSubContext must be used within a DropdownMenuSub')
  }
  return context
}

function DropdownMenuSubImpritive({ children, className, ...props }: React.HTMLProps<HTMLDivElement>) {
  const { open = false, setOpen: onOpenChange = () => {} } = usePopoverContext()
  const { wrapperRef, groupsRef, itemsRef, selectedItemRef, originalItemsRef, triggerRef, contentRef } =
    useDropdownMenuInit(open, onOpenChange, true)

  useHandleKeyDown({
    open,
    itemsRef,
    selectedItem: selectedItemRef.current,
    setSelectedItem: (item) => {
      selectedItemRef.current = item
    },
    originalItemsRef,
    allowAxisArrowKeys: true,
  })

  return (
    <DropdownMenuSubContext.Provider
      value={{
        open,
        onOpenChange,
        wrapperRef,
        triggerRef: triggerRef as never,
        contentRef: contentRef as never,
        groupsRef,
        itemsRef,
        selectedItemRef,
        originalItemsRef,
      }}>
      <div
        className={cn(
          'relative focus:bg-secondary [&>button]:focus:bg-secondary [&[aria-selected]:focus-visible>button]:bg-secondary [&[aria-selected]>button]:bg-secondary',
        )}
        {...props}
        duck-dropdown-menu-sub=""
        ref={wrapperRef}>
        {children}
      </div>
    </DropdownMenuSubContext.Provider>
  )
}

function DropdownMenuSub({ children, ...props }: React.ComponentPropsWithoutRef<typeof Popover>): React.JSX.Element {
  return (
    <Popover {...props} modal placement="right">
      <DropdownMenuSubImpritive {...props}>{children}</DropdownMenuSubImpritive>
    </Popover>
  )
}

function DropdownMenuSubTrigger({
  className,
  children,
  inset = false,
  ...props
}: React.ComponentPropsWithoutRef<typeof PopoverTrigger> & { inset?: boolean }) {
  return (
    <PopoverTrigger
      className={cn(
        'flex w-full items-center justify-between rounded-sm px-2 py-1.5 text-sm outline-none hover:opacity-100 [&_svg]:size-4',
        '[&[aria-selected]]:bg-secondary',
        '[&[data-open="true"]+div]:opacity-100',
        'data-[open=true]:bg-secondary',
        'focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-transparent',
        inset && 'pl-8',
        className,
      )}
      {...props}
      duck-dropdown-menu-sub-trigger="">
      {children}
      {<ChevronRight className="rtl:-ml-2 ltr:-mr-1 ltr:rotate-0 rtl:rotate-180" />}
    </PopoverTrigger>
  )
}

function DropdownMenuSubContent({
  className,
  children,
  ...props
}: React.ComponentPropsWithoutRef<typeof PopoverContent>) {
  const { contentRef } = useDropdownMenuSubContext()
  return (
    <PopoverContent
      ref={contentRef}
      className={cn('fixed z-[55] min-w-[8rem] p-1', className)}
      {...props}
      duck-dropdown-menu-sub-content="">
      {children}
    </PopoverContent>
  )
}

function DropdownMenuCheckboxItem({
  className,
  children,
  checked,
  onClick,
  onCheckedChange,
  ref,
  ...props
}: React.ComponentPropsWithRef<typeof Button> & { checked?: boolean; onCheckedChange?: (checked: boolean) => void }) {
  const [checkedState, setCheckedState] = React.useState(checked ?? false)
  return (
    <DropdownMenuItem
      onClick={(e) => {
        onClick?.(e)
        setCheckedState(!checkedState)
        onCheckedChange?.(!checkedState)
      }}
      duck-dropdown-menu-checkbox-item=""
      data-checked={checkedState}
      ref={ref}
      className={cn(className)}
      {...props}>
      <span className="absolute left-2.5 flex items-center">
        <Check className={cn('!size-4 opacity-0', checkedState && 'opacity-100')} />
      </span>
      <span className="ltr:pl-7 rtl:pr-7">{children}</span>
    </DropdownMenuItem>
  )
}

function DropdownMenuRadioGroup({ ...props }: React.ComponentPropsWithRef<typeof RadioGroup>) {
  return <RadioGroup duck-dropdown-menu-radio-group="" duck-dropdown-menu-group="" {...props} />
}

function DropdownMenuRadioItem({ ...props }: React.ComponentPropsWithRef<typeof RadioGroupItem>) {
  const groupItemRef = React.useRef<HTMLLIElement>(null)

  return (
    <DropdownMenuItem
      duck-dropdown-menu-item=""
      duck-dropdown-menu-radio-item=""
      onClick={() => {
        groupItemRef.current?.querySelector('label')?.click()
      }}>
      <RadioGroupItem
        ref={groupItemRef}
        {...props}
        className="ltr:pl-[1.25rem] rtl:pr-[1.25rem]"
        customIndicator={
          <span className="-translate-y-1/2 absolute top-1/2 flex size-2 rounded-full bg-foreground transition-all duration-150 ease-in-out ltr:left-1 rtl:right-1" />
        }
      />
    </DropdownMenuItem>
  )
}

export {
  DropdownMenu,
  DropdownMenuTrigger,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuCheckboxItem,
  DropdownMenuRadioItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuShortcut,
  DropdownMenuGroup,
  DropdownMenuSub,
  DropdownMenuSubTrigger,
  DropdownMenuSubContent,
  DropdownMenuRadioGroup,
}

export interface DropdownMenuShortcutProps extends React.HTMLProps<HTMLElement> {
  colored?: boolean
  /** The keyboard shortcut keys (e.g., "ctrl+K"). */
  keys: string
  /** Callback function that is invoked when the shortcut keys are pressed. */
  onKeysPressed: () => void
}

export interface DropdownMenuContextType {
  /** Ref to the dropdown wrapper element */
  wrapperRef: React.RefObject<HTMLDivElement | null>
  /** Ref to the button that opens the menu */
  triggerRef: React.RefObject<HTMLButtonElement | null>
  /** Ref to the menu content container */
  contentRef: React.RefObject<HTMLDivElement | null>
  /** Refs to all group containers inside the menu */
  groupsRef: React.RefObject<HTMLDivElement[]>
  /** Refs to all currently visible menu items */
  itemsRef: React.RefObject<HTMLLIElement[]>
  /** Refs to the original unfiltered menu items */
  originalItemsRef: React.RefObject<HTMLLIElement[]>
  /** Ref to the currently selected/focused item */
  selectedItemRef: React.RefObject<HTMLLIElement | null>
  /** Whether the menu is currently open */
  open: boolean
  /** Callback function that is invoked when the menu is opened or closed */
  onOpenChange: (open: boolean) => void
}

export type DropdownMenuSubContextType = DropdownMenuContextType
