import { cva } from '@gentleduck/variants'

export const toggleVariants = cva(
  'inline-flex items-center justify-center gap-2 rounded-md font-medium text-sm ring-offset-background transition-colors hover:bg-muted hover:text-muted-foreground focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 data-[state=on]:bg-accent data-[state=on]:text-accent-foreground [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0',
  {
    variants: {
      variant: {
        default: 'bg-transparent',
        outline: 'border border-input bg-transparent hover:bg-accent hover:text-accent-foreground',
      },
      size: {
        default: 'h-10 min-w-10 px-3',
        sm: 'h-9 min-w-9 px-2.5',
        lg: 'h-11 min-w-11 px-5',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  },
)

'use client'

import * as React from 'react'

import { toggleVariants } from './toggle.constants'
import { VariantProps } from '@gentleduck/variants'
import { cn } from '@gentleduck/libs/cn'

function Toggle({
  className,
  variant,
  value,
  ref,
  children,
  ...props
}: Omit<React.HTMLProps<HTMLInputElement>, 'size'> & Omit<VariantProps<typeof toggleVariants>, 'size'>) {
  return (
    <label className={cn('relative flex items-center justify-center')} duck-toggle="" data-value={value}>
      <input
        type="checkbox"
        ref={ref}
        value={value}
        className={cn(toggleVariants({ variant, className }), 'appearance-none checked:bg-muted')}
        {...props}
      />
      <span className={cn('pointer-events-none absolute isolate z-1 select-none')}>{children}</span>
    </label>
  )
}

export { Toggle }
